import Layout from '../../components/MyLayout';
import fetch from 'isomorphic-unfetch';

const Post = props => (

  <Layout>
         <style jsx>{`
html, body{
    width: 100%;
    height: 100%;
    margin: 0;
    padding: 0;
    background-color: #ddd;
  font-family: 'Arimo', sans-serif;
}

.title{
  text-align: center;
  color: #2c3e50;
}

/* CONTAINERS */ 

.container {max-width: 940px; width: 100%; margin: 0 auto; } 
.twelve { width: 100%; } 
.eleven { width: 91.53%; } 
.ten { width: 83.06%; } 
.nine { width: 74.6%; } 
.eight { width: 66.13%; } 
.seven { width: 57.66%; } 
.six { width: 49.2%; } 
.five { width: 40.73%; } 
.four { width: 32.26%;} 
.three { width: 23.8%; } 
.two { width: 15.33%; } 
.one{ width: 6.866%;}

 /* COLUMNS */ 

.col { 
    display: block; 
    float:left;
    margin: 1% 0 1% 1.6%; 
} 

.col:first-of-type { margin-left: 0; }

 /* CLEARFIX */ 

 .cf:before, .cf:after { 
    content: " "; 
    display: table; 
 } 

 .cf:after {
    clear: both; 
} 

 .cf { 
    *zoom: 1; 
 }

/* PROFILES */

.card{
    max-width: 100%;
    height: 400px;
    background-color: #fff;
    font-family: 'Arimo', sans-serif;
    font-size: 14px;
}

/* CARD ! */

#card1{
    text-align: center;
    color: #2c3e50;
    padding: 15px;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
}

#card1 .image-wrapper{
    width: 100px;
    height: 100px;
    margin: 20px auto;
    border-radius: 100%;
    background-image: url("https://static.pexels.com/photos/7096/people-woman-coffee-meeting.jpg");
    background-size: cover;
    background-repeat: no-repeat;
}

#card1 .info .four{
    text-align: center;
    border-right: 1px solid #2c3e50;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
}

#card1 .info .four:last-of-type{
    border-right: none;
}

#card1 .info .four .number{
    display: block;
    font-size: 20px;
    padding: 3px 0;
    font-weight: 700;
}

#card1 .options{
    margin-top: 30px;
    text-align: left;
}

#card1 .options ul{
    list-style-type: none;
    padding: 0;
    margin: 0;
}

#card1 .options ul .icon{
    display: inline-block;
    width: 30px;
    height: 30px;
    background-color: #3498db;
    border-radius: 100%;
    margin-right: 8px;
    vertical-align: middle;
