import Layout from '../../components/MyLayout';
import fetch from 'isomorphic-unfetch';

const Post = props => (

  <Layout>

    <h1>{props.characters.name}</h1>
    {/* <p>{props.characters.summary.replace(/<[/]?[pb]>/g, '')}</p> */}

    <img src={props.characters.images.lg} />
    <h3>Power Stats</h3>
    <ul>
        <li>Intelligence : {props.characters.powerstats.intelligence}</li>
        <li> Strength : {props.characters.powerstats.strength}</li>
        <li>Speed : {props.characters.powerstats.speed}</li>
        <li>Power : {props.characters.powerstats.power}</li>
    </ul>

    <h3>Biography</h3>
    <ul>
        <li>Name : {props.characters.biography.fullName}</li>
        <li>Alter Egos: {props.characters.biography.alterEgos}</li>
        <li>Publisher :{props.characters.biography.publisher}</li>
        <li>Good or Bad: {props.characters.biography.alignment}</li>
    </ul>

    HTML CSSResult
<div className="container">
  <h1 className="title">User Profile Cards</h1>
  <div className="row cf">
    <div id="card1" className="card four col">
      <div className="image-wrapper"></div>
      <h3 className="name">Amy Smith</h3>
      <div className="info cf">
        <div className="four col"><span className="number">100</span>Posts</div>
        <div className="four col"><span className="number">28</span>Tasks</div>
        <div className="four col"><span className="number">179</span>Likes</div>
      </div>
      <div className="options">
        <ul>
          <li><span className="icon"><i className="fa fa-plus" aria-hidden="true"></i></span>Add to team</li>
          <li><span className="icon"><i className="fa fa-envelope" aria-hidden="true"></i></span>Send a message</li>
        </ul>
      </div>
    </div>

    <div id="card2" className="card four col">
      <div className="header"></div>
      <div className="wrapper">
        <div className="image-wrapper"></div>
        <h3 className="name">Jack Smith</h3>
        <div className="social cf">
          <div className="four col">
            <a href="#"><i className="fa fa-facebook" aria-hidden="true"></i></a>
          </div>
          <div className="four col">
            <a href="#"><i className="fa fa-twitter" aria-hidden="true"></i></a>
          </div>
          <div className="four col">
            <a href="#"><i className="fa fa-linkedin" aria-hidden="true"></i></a>
          </div>
        </div>
        <div className="info">
          <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit. Voluptatem aspernatur, mollitia suscipit perspiciatis minima reprehenderit quasi consectetur.</p>
        </div>
      </div>
    </div>

    <div id="card3" className="card four col">
      <div className="header"></div>
      <div className="wrapper">
        <h2 className="name">John Smith</h2>
      </div>
      <div className="info cf">
        <div className="four col">
          <div className="number">17</div>

        </div>
        <div className="four col">
          <div className="number">239</div>
        </div>
        <div className="four col">
          <div className="number">8</div>
        </div>
      </div>
    </div>
  </div>
</div>
<style jsx>{`
html, body{
    width: 100%;
    height: 100%;
    margin: 0;
    padding: 0;
    background-color: #ddd;
  font-family: 'Arimo', sans-serif;
}

.title{
  text-align: center;
  color: #2c3e50;
}

/* CONTAINERS */ 

.container {max-width: 940px; width: 100%; margin: 0 auto; } 
.twelve { width: 100%; } 
.eleven { width: 91.53%; } 
.ten { width: 83.06%; } 
.nine { width: 74.6%; } 
.eight { width: 66.13%; } 
.seven { width: 57.66%; } 
.six { width: 49.2%; } 
.five { width: 40.73%; } 
.four { width: 32.26%;} 
.three { width: 23.8%; } 
.two { width: 15.33%; } 
.one{ width: 6.866%;}

 /* COLUMNS */ 

.col { 
    display: block; 
    float:left;
    margin: 1% 0 1% 1.6%; 
} 

.col:first-of-type { margin-left: 0; }

 /* CLEARFIX */ 

 .cf:before, .cf:after { 
    content: " "; 
