import Layout from '../components/MyLayout';
import Link from 'next/link';
import fetch from 'isomorphic-unfetch';

const Index = props => (
  <Layout>
    <div id="container">
    <div id="row">
    <h1>Superheros</h1>

    <ul className="grid">
      {props.shows.map(characters => (
        <li key={characters.id}>
          <Link href="/p/[id]" as={`/p/${characters.id}`}>
            <a>{characters.name}</a>
          </Link>
        </li>
      ))}
    </ul>

    <table>
        <thead>
          <tr>
              <th>Name</th>
              <th>Item Name</th>
              <th>Item Price</th>
          </tr>
        </thead>

        <tbody>
          <tr>
            <td>Alvin</td>
            <td>Eclair</td>
            <td>$0.87</td>
          </tr>
          <tr>
            <td>Alan</td>
            <td>Jellybean</td>
            <td>$3.76</td>
          </tr>
          <tr>
            <td>Jonathan</td>
            <td>Lollipop</td>
            <td>$7.00</td>
          </tr>
        </tbody>
      </table>


    </div>
    </div>
      <style jsx>{`
      #container ul { list-style: none; }
      #container .grid li { float: left; width: 20%; height: 50px; border-right: 1px dotted #CCC; border-bottom: 1px dotted #CCC; padding: 20px; }
      `}</style>
  </Layout>
);

Index.getInitialProps = async function() {
  const res = await fetch('https://raw.githubusercontent.com/akabab/superhero-api/0.2.0/api/all.json');
  const data = await res.json();

  console.log(`Show data fetched. Count: ${data.length}`);

  return {
    shows: data.map(entry => entry)

  };

};

export default Index;
